contracts/PublicHealthReports.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// Contract 2: Public Health Reports (Open for Verification)
contract PublicHealthReports {
    struct HealthReport {
        bytes32 dataHash;
        string reportType; // "outbreak", "vaccination_campaign", "health_survey"
        string location; // "District A", "Rural Area B"
        address submittedBy; // Health worker, NGO, government official
        uint256 timestamp;
        string ipfsHash; // For detailed data
        bool isActive;
    }

    mapping(bytes32 => HealthReport) public reports;
    mapping(string => bytes32[]) public reportsByLocation;
    mapping(string => bytes32[]) public reportsByType;

    event ReportSubmitted(
        bytes32 indexed reportHash,
        string indexed location,
        string reportType,
        address indexed submitter
    );

    function submitHealthReport(
        bytes32 _dataHash,
        string memory _reportType,
        string memory _location,
        string memory _ipfsHash
    ) external  {
        reports[_dataHash] = HealthReport({
            dataHash: _dataHash,
            reportType: _reportType,
            location: _location,
            submittedBy: msg.sender,
            timestamp: block.timestamp,
            ipfsHash: _ipfsHash,
            isActive: true
        });

        reportsByLocation[_location].push(_dataHash);
        reportsByType[_reportType].push(_dataHash);

        emit ReportSubmitted(_dataHash, _location, _reportType, msg.sender);
    }

    // Anyone can verify public health reports
    function verifyHealthReport(
        bytes32 _dataHash
    ) external view returns (bool exists, HealthReport memory report) {
        exists =
            reports[_dataHash].timestamp != 0 &&
            reports[_dataHash].isActive;
        if (exists) {
            report = reports[_dataHash];
        }
    }

    // Get all outbreak reports for a location (for dashboards)
    function getReportsByLocation(
        string memory _location
    ) external view returns (bytes32[] memory) {
        return reportsByLocation[_location];
    }

    // Get all reports by type (for analytics)
    function getReportsByType(
        string memory _reportType
    ) external view returns (bytes32[] memory) {
        return reportsByType[_reportType];
    }
}
